import { useState } from "react";
import { format, addDays } from "date-fns";

const startDate = new Date("2024-06-17");
const names = ["Bosca", "Mraco"];

function getOwner(date) {
  const daysSinceStart = Math.floor((date - startDate) / (1000 * 60 * 60 * 24));
  return names[daysSinceStart % 2 === 0 ? 0 : 1];
}

export default function App() {
  const today = new Date();
  const [daysToShow] = useState(14);

  const calendarDays = Array.from({ length: daysToShow }, (_, i) => {
    const day = addDays(today, i - 7);
    return {
      date: day,
      owner: getOwner(day),
      isToday: format(day, "yyyy-MM-dd") === format(today, "yyyy-MM-dd"),
    };
  });

  return (
    <div className="p-6 max-w-xl mx-auto font-sans">
      <h1 className="text-2xl font-bold mb-4">ðŸ”¥ Giorno della Torcia</h1>
      <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 mb-6">
        <p className="text-lg">Oggi la torcia Ã¨ di <strong>{getOwner(today)}</strong>!</p>
      </div>

      <h2 className="text-xl font-semibold mb-2">Calendario</h2>
      <div className="grid grid-cols-2 gap-2">
        {calendarDays.map(({ date, owner, isToday }) => (
          <div
            key={date}
            className={'p-3 rounded-xl shadow text-sm \${isToday ? "bg-blue-100 border border-blue-400" : "bg-gray-100"}'}
          >
            <div className="font-semibold">{format(date, "dd/MM/yyyy")}</div>
            <div>Torcia: {owner}</div>
            {isToday && <div className="text-blue-700 text-xs">(Oggi)</div>}
          </div>
        ))}
      </div>
    </div>
  );
}
