import { useState } from "react";
import { format, addDays } from "date-fns";

const startDate = new Date("2024-06-17");
const names = ["Bosca", "Mraco"];

function getOwner(date) {
  const daysSinceStart = Math.floor((date - startDate) / (1000 * 60 * 60 * 24));
  return names[daysSinceStart % 2 === 0 ? 0 : 1];
}

export default function App() {
  const today = new Date();
  const [daysToShow] = useState(14);
  const [isTorchOn, setIsTorchOn] = useState(false);

  const calendarDays = Array.from({ length: daysToShow }, (_, i) => {
    const day = addDays(today, i - 7);
    return {
      date: day,
      owner: getOwner(day),
      isToday: format(day, "yyyy-MM-dd") === format(today, "yyyy-MM-dd"),
    };
  });

  const toggleTorch = () => {
    setIsTorchOn(!isTorchOn);
  };

  const getDayName = (date) => {
    const days = ['Domenica', 'LunedÃ¬', 'MartedÃ¬', 'MercoledÃ¬', 'GiovedÃ¬', 'VenerdÃ¬', 'Sabato'];
    return days[date.getDay()];
  };

  return (
    <div className="p-6 max-w-2xl mx-auto font-sans bg-gray-50 min-h-screen">
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">
          ðŸ”¦ Torcia App v2.0
        </h1>

        {/* Sezione proprietario */}
        <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 mb-6 rounded">
          <p className="text-lg">
            Oggi la torcia Ã¨ di <strong className="text-xl">{getOwner(today)}</strong>!
          </p>
        </div>

        {/* Sezione torcia interattiva */}
        <div className="text-center mb-8">
          <h2 className="text-2xl font-semibold mb-4 text-gray-800">Controlla la Torcia</h2>
          <div className="bg-gray-100 p-6 rounded-lg shadow-inner">
            <img 
              src={isTorchOn ? "/images/torcia-accesa.png" : "/images/torcia-spenta.png"}
              alt={isTorchOn ? "Torcia accesa" : "Torcia spenta"}
              className="w-48 h-64 object-contain mx-auto mb-4 transition-all duration-300"
            />
            <button
              onClick={toggleTorch}
              className={`px-8 py-3 rounded-full text-white font-semibold text-lg transition-all duration-300 transform hover:scale-105 ${
                isTorchOn 
                  ? "bg-red-500 hover:bg-red-600 shadow-lg" 
                  : "bg-green-500 hover:bg-green-600 shadow-lg"
              }`}
            >
              {isTorchOn ? "ðŸ”´ Spegni Torcia" : "ðŸŸ¢ Accendi Torcia"}
            </button>
            <p className="mt-3 text-gray-600">
              Stato: <span className={`font-semibold ${isTorchOn ? "text-green-600" : "text-gray-500"}`}>
                {isTorchOn ? "ACCESA âœ¨" : "SPENTA"}
              </span>
            </p>
          </div>
        </div>

        {/* Sezione calendario */}
        <div>
          <h2 className="text-2xl font-semibold mb-4 text-gray-800">
            ðŸ“… Calendario della Torcia
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
            {calendarDays.map(({ date, owner, isToday }) => (
              <div
                key={date}
                className={`p-4 rounded-lg shadow-sm border transition-all duration-200 hover:shadow-md ${
                  isToday 
                    ? "bg-blue-50 border-blue-300 shadow-md" 
                    : "bg-white border-gray-200 hover:bg-gray-50"
                }`}
              >
                <div className="flex justify-between items-center">
                  <div>
                    <div className="font-semibold text-gray-800">
                      {format(date, "dd/MM/yyyy")}
                    </div>
                    <div className="text-sm text-gray-600">
                      {getDayName(date)}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className={`font-medium ${
                      owner === "Bosca" ? "text-blue-600" : "text-green-600"
                    }`}>
                      {owner}
                    </div>
                    {isToday && (
                      <div className="text-xs bg-blue-100 text-blue-700 px-2 py-1 rounded-full">
                        OGGI
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-8 text-gray-500 text-sm">
          <p>Torcia App v2.0 - Sistema di gestione torcia condivisa</p>
        </div>
      </div>
    </div>
  );
}
